// Pega o primeiro item que chega (que contém a lista 'data')
const item = items[0];

// Pega a lista de transações de dentro do campo 'data'
const transactions = item.json.data;

// Verifica se 'transactions' é de fato uma lista para evitar erros
if (!Array.isArray(transactions)) {
  return []; // Retorna vazio se não for uma lista
}

// Transforma cada transação da lista em um item separado para o n8n
// e retorna todos eles.
return transactions.map(transaction => {
  return {
    json: transaction
  };
});
