// Pega o item que vem do nó anterior
const item = $input.item;
const campanhas = item.json.campaigns;

// Cria uma nova lista de itens
const novosItens = [];

// Loop para "desempacotar" cada campanha
for (const campanha of campanhas) {
  // Cria um novo item n8n para cada campanha
  novosItens.push({ json: campanha });
}

// Retorna a nova lista de itens
// O n8n vai processar cada item um por um
return novosItens;

